package DoubleWeekly75;

public class Q3 {
	public static void main(String[] args) {
		Solution test = new Q3().new Solution();
		System.out.println(test.numberOfWays(""));
	}
	class Solution {
		public long numberOfWays(String s) {
			char[] rec = s.toCharArray();
			int[] help = new int[s.length()];
			int e = 0;
			long res = 0;
			help[e] = 1;

			for (int i = 1; i < s.length(); i++) {
				if (s.charAt(i) == s.charAt(i - 1)) help[e]++;
				else help[++e]++;
			}
			if (help[3] == 0) return 0;
			for (int i = 0; i < e - 1; i++) {
				for (int j = i + 1; j+1 <= e;j+=2) {
					long temp = 0;
					for (int k = j + 1; k <= e; k += 2) {
						temp += help[k];
					}
					res += help[i] * help[j] * temp;
				}
			}
			return res;
		}
	}
}
